// Dependencies
import { useContext, useEffect, useRef, useState } from 'react';
import Router from 'next/router';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Box, Flex, Grid } from '@chakra-ui/core';
import { MdAddShoppingCart } from 'react-icons/md';

// Helpers
import { ADD_ITEM_MUTATION } from '../lib/apollo/mutations';
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { StyledButton, StyledInput } from './styles/styled';

// Hooks + Contexts
import { UserContext } from '../lib/React/UserContext';

const GroceryForm = () => {
  // Form Refs
  const productRef = useRef('');
  const quantityRef = useRef('');

  // Hooks + Contexts
  const { user } = useContext(UserContext);
  const [isCalling, setIsCalling] = useState(false);

  // Apollo
  const [addItem, itemRes] = useMutation(ADD_ITEM_MUTATION);

  // useEffect
  useEffect(() => {
    // Force the user to sign in
    if (!user) Router.push('/signin');
  }, [user]);

  // Handlers
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsCalling(true);

    const input = {
      product: productRef.current.value,
      quantity: parseInt(quantityRef.current.value)
    };

    addItem({
      variables: { input },
      optimisticResponse: {
        __typename: 'Mutation',
        addItem: {
          item: {
            id: Math.round(Math.random() * -1000000),
            ...input
          }
        }
      },
      update: (cache, { data: { addItem } }) => {
        const { currentUserItems } = cache.readQuery({
          query: USERS_ITEMS_QUERY
        });
        const { nodes } = currentUserItems;
        const { item } = addItem;

        cache.writeQuery({
          query: USERS_ITEMS_QUERY,
          data: { currentUserItems: { nodes: [item, ...nodes] } }
        });
      }
    });

    setIsCalling(false);
    productRef.current.value = '';
    quantityRef.current.value = '';

    productRef.current.focus();
  };

  return (
    <Flex justify="center">
      <form method="post" onSubmit={handleSubmit}>
        <Grid templateColumns="5fr 1fr">
          <Box>
            <StyledInput
              type="text"
              placeholder="Product"
              isDisabled={isCalling}
              ref={productRef}
              isRequired
            />
            <StyledInput
              type="number"
              placeholder="Quantity"
              isDisabled={isCalling}
              ref={quantityRef}
              isRequired
            />
          </Box>
          <StyledButton
            type="submit"
            variantColor="blue"
            icon={MdAddShoppingCart}
            isLoading={isCalling}
            isDisabled={isCalling}
            aira-busy={isCalling}
            height
          />
        </Grid>
      </form>
    </Flex>
  );
};

export default GroceryForm;
