// Dependencies
import { useRef, useState } from 'react';
import {
  Box,
  Button,
  InputGroup,
  InputRightElement,
  Stack
} from '@chakra-ui/core';

// Styles
import { AiOutlineForm } from 'react-icons/ai';
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput } from './styles/styled';

// Component
import FormLayout from '../lib/FormLayout';

const SignInForm = () => {
  const ref = useRef('');
  const [status, setStatus] = useState(false);
  const [showPassword, setShow] = useState(false);
  const [showRegistration, setReg] = useState(false);

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <FormLayout>
        <Box>
          <StyledInput
            type="email"
            placeholder="Enter email"
            isDisabled={status}
          />

          <InputGroup size="md">
            <StyledInput
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Enter password"
              isDisabled={status}
            />
            {!showRegistration && (
              <InputRightElement width="4.5rem">
                <Button
                  h="1.75rem"
                  size="sm"
                  onClick={() => setShow(!showPassword)}
                >
                  {showPassword ? 'Hide' : 'Show'}
                </Button>
              </InputRightElement>
            )}
          </InputGroup>

          <InputGroup size="md">
            <StyledInput
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Confirm password"
              isDisabled={status}
            />
            <InputRightElement width="4.5rem">
              <Button
                h="1.75rem"
                size="sm"
                onClick={() => setShow(!showPassword)}
              >
                {showPassword ? 'Hide' : 'Show'}
              </Button>
            </InputRightElement>
          </InputGroup>
        </Box>
        <StyledButton
          type="submit"
          variantColor="blue"
          icon={FiLogIn}
          isLoading={status}
          isDisabled={status}
          aira-busy={status}
          height
        />
      </FormLayout>
    </Stack>
  );
};

export default SignInForm;
