import React, { useState, useRef } from 'react';
import { Flex, Heading, Stack, Input, IconButton, Grid } from '@chakra-ui/core';
import { MdAddShoppingCart } from 'react-icons/md';

const GroceryForm = () => {
  const productRef = useRef('');
  const quantityRef = useRef('');
  const [status, setStatus] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus(true);

    setTimeout(() => {
      setStatus(false);
    }, 2000);

    console.log('product');
    console.log(productRef.current.value);
    console.log('quantity');
    console.log(quantityRef.current.value);
  };

  return (
    <Stack spacing="2rem">
      <Heading as="h1" size="2xl">
        Add Items
      </Heading>
      <Flex>
        <form onSubmit={handleSubmit}>
          <Grid templateColumns="5fr 1fr">
            <Box>
              <Input type="text" placeholder="Product" ref={productRef} />
              <Input type="number" placeholder="Quantity" ref={quantityRef} />
            </Box>
            <IconButton
              type="submit"
              icon={MdAddShoppingCart}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
            />
          </Grid>
        </form>
      </Flex>
    </Stack>
  );
};

export default GroceryForm;
