// Dependencies
import { useContext, useState } from 'react';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Button } from '@chakra-ui/core';

// Apollo
import { ADD_TO_HISTORY_MUTATION } from '../lib/apollo/mutations';

// React
import { StatusContext } from '../lib/React/StatusContext';

const GroceryCheckout = () => {
  // Hooks + Contexts
  const [isCalling, setIsCalling] = useState(false);
  const { status } = useContext(StatusContext);

  // Apollo
  const [addToHistory, addToHistoryResponse] = useMutation(
    ADD_TO_HISTORY_MUTATION
  );

  // Handlers
  const handleCheckout = () => {
    setIsCalling(true);

    console.log(status);

    addToHistory({
      variables: { input: status },

      optimisticResponse: {
        __typename: 'Mutation',
        addToHistory: {
          item: {
            id: Math.round(Math.random() * -1000000),
            ...input
          }
        }
      }
    });

    setIsCalling(false);
  };

  return (
    <Button
      variantColor="blue"
      alignSelf="center"
      width="35%"
      maxWidth="300px"
      marginBottom="2rem"
      onClick={handleCheckout}
      isLoading={isCalling}
      isDisabled={isCalling}
      aira-busy={isCalling}
      aria-label={`Checkout the selected items`}
    >
      Checkout
    </Button>
  );
};

export default GroceryCheckout;
