import { Heading, Stack } from '@chakra-ui/core';

const Loading = (props) => (
  <Stack
    width="60%"
    maxWidth="500px"
    minWidth="300px"
    margin="auto"
    spacing="0.5rem"
  >
    {!props.status && (
      <Heading as="h4" size="lg">
        Loading
      </Heading>
    )}
    {props.children}
    {props.children}
    {props.children}
    {props.children}
  </Stack>
);

export default Loading;
