import { createContext, useState } from 'react';

const ViewContext = createContext({});

const View = (props) => {
  const [isAdd, setIsAdd] = useState(true);

  return (
    <UserContext.Provider value={{ isAdd, setIsAdd }}>
      {props.children}
    </UserContext.Provider>
  );
};

export default View;
