import { createContext } from 'react';

const StatusContext = createContext({});

const Status = (props) => {
  state = { status: [] };

  addToHistory = (id, status) => {
    this.setState(
      { status: [{ id, status }].concat(this.state.status) },
      () => {
        console.log('StatusContext - addToHistory');
        console.log(this.state.status);
      }
    );
  };

  removeFromHistory = (id) => {
    const newHistory = this.state.status.filter((item) => id !== item.id);

    this.setState({ status: newHistory }, () => {
      console.log('StatusContext - removeFromHistory');
      console.log(this.state.status);
    });
  };

  return (
    <StatusContext.Provider value={{ addToHistory, removeFromHistory }}>
      {props.children}
    </StatusContext.Provider>
  );
};

export default Status;

export { StatusContext };
