import Cookies from 'js-cookie';

/**
 * handleLogin Function
 * Used to determine if the login is valid or not
 * @param {*} tokenRes
 * This is the response from the server that may
 * or may not contain the  JWT
 * @param {*} setErrorHook
 * Used to set the error state in the component
 * For when the JWT response is null to set and error
 */
const handleLogin = (tokenRes, setErrorHook) => {
  if (!tokenRes.data.authenticate.jwtToken) {
    /**
     * When the response evaluates to null
     * Will come in here to set the error
     */
    console.log('handleLogin - Erroring');
    setErrorHook('Invalid credentials from here');
  } else {
    /**
     * When the response is not null,
     * Set the cookeis with the jwtToken data
     */
    console.log('handleLogin - Good');
    Cookies.set('grocerylist_auth_token', tokenRes.data.authenticate.jwtToken);
  }
};

export default handleLogin;
