import React, { createContext, Component } from 'react';

const StatusContext = createContext({});

class Status extends Component {
  state = { status: [] };

  addToHistory = (id, status) => {
    this.setState({ status: [{ id, status }].concat(this.state.status) });
  };

  removeFromHistory = (id) => {
    const newHistory = this.state.status.filter((item) => id !== item.id);

    this.setState({ status: newHistory });
  };

  render() {
    const { addToHistory, removeFromHistory } = this;
    const value = { addToHistory, removeFromHistory };

    return (
      <StatusContext.Provider value={value}>
        {this.props.children}
      </StatusContext.Provider>
    );
  }
}

export default Status;

export { StatusContext };
