import { useRef, useState } from 'react';
import { Box, Grid, Stack, Input, IconButton } from '@chakra-ui/core';

// Styles
import { StyledButton, StyledInput } from './styles/styled';

// Component
import FormLayout from '../lib/FormLayout';

const SignInForm = () => {
  const ref = useRef('');
  const [status, setStatus] = useState(false);

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <FormLayout>
        <Box>
          <StyledInput type="text" placeholder="Product" isDisabled={status} />
          <StyledInput
            type="number"
            placeholder="Quantity"
            isDisabled={status}
          />
        </Box>
        <StyledButton
          type="submit"
          variantColor="blue"
          icon={IoMdLogIn}
          isLoading={status}
          isDisabled={status}
          aira-busy={status}
          height
        />
      </FormLayout>
    </Stack>
  );
};

export default SignInForm;
