// Dependencies
import { useState } from 'react';
import moment from 'moment';

// Styles
import {
  Accordion,
  AccordionItem,
  AccordionPanel,
  AccordionIcon,
  Box,
  Text,
  Skeleton
} from '@chakra-ui/core';
import { StyleAccHeader } from './styles/styled';

const HistoryList = ({ id, history, createdAt }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Skeleton>
      <Accordion
        background="white"
        border="1px solid black"
        borderRadius="1rem"
        marginBottom="1rem"
      >
        <AccordionItem>
          <StyleAccHeader onClick={() => setIsOpen(!isOpen)}>
            <Box flex="1" textAlign="left">
              <Text fontSize="2xl">Order No. {id}</Text>
              <Text fontSize="lg">
                {moment(createdAt, 'YYYYMMDD').fromNow()}
              </Text>
            </Box>
            <AccordionIcon />
          </StyleAccHeader>
          <AccordionPanel pb={4} isOpen={isOpen}>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
          </AccordionPanel>
        </AccordionItem>
      </Accordion>
    </Skeleton>
  );
};

export default HistoryList;
