import Link from 'next/link';
import { useRouter } from 'next/router';

import { Flex, List, ListItem, Heading } from '@chakra-ui/core';

const Nav = () => {
  const { pathname } = useRouter();

  return (
    <List as={Flex} justifyContent="space-evenly" paddingBottom="2rem">
      <ListItem>
        <Link href="/history">
          <a>
            <Heading
              as="h1"
              size="2xl"
              as={router.pathname === '/history' && 'u'}
            >
              History
            </Heading>
          </a>
        </Link>
      </ListItem>
      <ListItem>
        <Link href="/">
          <a>
            <Heading as="h1" size="2xl" as={router.pathname === '/' && 'u'}>
              Home
            </Heading>
          </a>
        </Link>
      </ListItem>
    </List>
  );
};

export default Nav;
