import styled from '@emotion/styled';
import {
  Accordion,
  AccordionHeader,
  Box,
  IconButton,
  Input
} from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledBox = styled(Box)`
  ::before {
    border-style: none;
  }
  ::after {
    border-style: none;
  }
`;

export const StyleAccHeader = styled(AccordionHeader)`
  :focus {
    outline: 0 !important;
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0) !important;
  }

  -webkit-tap-highlight-color: transparent;
  -webkit-touch-callout: transparent;
  -webkit-user-select: transparent;
  -khtml-user-select: transparent;
  -moz-user-select: transparent;
  -ms-user-select: transparent;
  user-select: transparent;
`;
