// Dependencies
import { useContext, useEffect, useRef, useState } from 'react';
import Router from 'next/router';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Box, Flex, Grid, IconButton, Stack, Button } from '@chakra-ui/core';
import { MdAddShoppingCart, MdReceipt } from 'react-icons/md';

// Helpers
import { ADD_ITEM_MUTATION } from '../lib/apollo/mutations';
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { StyledButton, StyledInput } from './styles/styled';

// Hooks + Contexts
import { UserContext } from '../lib/React/UserContext';
import { ShoppingContext } from '../lib/React/ShoppingContext';



const GroceryFormLayout = () => {
  // Form Refs
  const productRef = useRef('');
  const quantityRef = useRef('');

  // Hooks + Contexts
  const { user } = useContext(UserContext);
  const { isShopping, setIsShopping } = useContext(ShoppingContext);
  const [isCalling, setIsCalling] = useState(false);

  // Apollo
  const [addItem, itemRes] = useMutation(ADD_ITEM_MUTATION);

  // useEffect
  useEffect(() => {
    // Force the user to sign in
    if (!user) Router.push('/signin');
  }, [user]);

  // Handlers
  const handleClick = () => {
    setIsShopping(!isShopping);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsCalling(true);

    const input = {
      product: productRef.current.value,
      quantity: parseInt(quantityRef.current.value)
    };

    addItem({
      variables: { input },
      optimisticResponse: {
        __typename: 'Mutation',
        addItem: {
          item: {
            id: Math.round(Math.random() * -1000000),
            ...input
          }
        }
      },
      update: (cache, { data: { addItem } }) => {
        const { currentUserItems } = cache.readQuery({
          query: USERS_ITEMS_QUERY
        });
        const { nodes } = currentUserItems;
        const { item } = addItem;

        cache.writeQuery({
          query: USERS_ITEMS_QUERY,
          data: { currentUserItems: { nodes: [item, ...nodes] } }
        });
      }
    });

    setIsCalling(false);
    productRef.current.value = '';
    quantityRef.current.value = '';

    productRef.current.focus();
  };

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <IconButton
        variantColor="blue"
        icon={isShopping ? MdAddShoppingCart : MdReceipt}
        width="65%"
        maxWidth="300px"
        alignSelf="center"
        onClick={handleClick}
        aria-label={`Click to switch from ${
          isShopping ? 'add to remove' : 'remove to add'
        }`}
      />
      {isShopping ? (
        <GroceryForm />
      ) : (
        <Button
          variantColor="blue"
          width="35%"
          maxWidth="300px"
          alignSelf="center"
          aria-label={`Checkout the selected items`}
        >
          Checkout
        </Button>
      )}
    </Stack>
  );
};

export default GroceryFormLayout;
